(107, 121, 1, 1, 0, NULL, NULL, NULL, 70, '2022-10-03 00:00:00', '2022-11-25 00:00:00', '2022-10-15 00:00:00', '2022-12-02 00:00:00', '2 ejercicios practicos para desarrollar en forma escrita\r\nPreguntas alusivas a los 2 ejercicios resueltos y conceptos teoricos \r\nDeben tener aprobado los TPs obligatorios', '2 ejercicios practicos para desarrollar en forma escrita a elegir entre 4 ejercicios propuestos.\r\nPreguntas alusivas a los 2 ejercicios resueltos y conceptos para poder resolver los otros 2 ejercicios mas preguntas conceptuales teoricas. \r\nDeben tener aprobado los TPs obligatorios'),
(108, 120, 1, 1, 1, '2022-12-07 00:00:00', '2022-12-09 00:00:00', 'El CFI consiste en una exposición y defensa de un tema desarrollado por el estudiante. Sobre el cual los docentes realizan consultas y preguntas integrales.', 80, '2022-10-17 00:00:00', '2022-11-24 00:00:00', '2022-11-30 00:00:00', '2022-11-30 00:00:00', 'Exámen teórico práctico con  desarrollo conceptual.', 'Exámen teórico práctico con resolución de problemas de ingeniería.');
INSERT INTO `planif_resultados_aprendizajes` (`id`, `planif_planificaciones_id`, `resultado`, `posicion`) VALUES
(4, 6, 'RA 1: Propone medidas estructurales y no estructurales para proteger las obras de captación de agua subterránea y los recursos de agua, considerando la delimitación de perímetros de protección y el uso del territorio (pasado, presente y futuro).', 0),
(5, 6, 'RA 2: Certifica el funcionamiento y estado de obras de captación de agua subterránea para distintos usos mediante el relevamiento de usos del territorio, categorización de las amenazas e inspecciones sanitarias de pozos.', 1),
(6, 6, 'RA 3: Elabora planes de gestión de los recursos hídricos subterráneos y superficiales, considerando situaciones de riesgo y peligro de contaminación, determinando lineamientos para definir el ordenamiento territorial y la definición de áreas protegidas.', 2),
(7, 6, 'RA 4: Selecciona las metodologías de remediación de acuíferos que resultan más apropiadas, considerando las características de los sitios contaminados, el contaminante, la presencia de fuentes de abastecimiento de agua y otros usuarios.', 3),
(8, 6, 'RA 5: Controla el resultado de la aplicación de metodologías de remediación de acuíferos, considerando las características de los sitios contaminados, el contaminante, la presencia de fuentes de abastecimiento de agua subterránea y otros usuarios', 4),
(9, 9, '- Los alumnos finalizado el curso, podrán reconocer los elementos que componen la planificación urbana de las ciudades, abordando los distintos problemas que enfrentan, en un contexto de actualización tecnológica e integración de la información.', 0),
